import { Component, OnInit } from '@angular/core';
import { AuthService } from '../auth.service';
import { EventService } from '../event.service';
import { Reservation } from '../reservation.model';

interface User {
  id: number;
  username: string;
  email: string;
  role: string;
}

interface Event {
  id: number;
  name: string;
  date: string | Date;
  location: string;
  isUserEvent?: boolean;
  description: string;
}

@Component({
  selector: 'app-user-dashboard',
  templateUrl: './user-dashboard.component.html',
  styleUrls: ['./user-dashboard.component.css']
})
export class UserDashboardComponent implements OnInit {
  userInfo: User | null = null;
  updatedUserInfo: Partial<User> = {};
  currentView: string = 'profile';
  events: Event[] = [];
  selectedEvents: Event[] = [];
  date: string = '';
  location: string = '';
  keyword: string = '';
  showReservationForm: boolean = false;
  selectedEventId: number | null = null;
  numberOfTickets: number = 1;

  constructor(
    private authService: AuthService,
    private eventService: EventService
  ) { }

  ngOnInit() {
    this.loadUserInfo();
    this.loadAllEvents();
  }

  parseDate(dateString: string): Date {
    const [datePart, timePart] = dateString.split(' ');
    const [day, month, year] = datePart.split('/').map(Number);
    const [hours, minutes] = timePart.split(':').map(Number);
    return new Date(year, month - 1, day, hours, minutes);
  }

  loadUserInfo() {
    const username = this.authService.getUsername();
    if (username) {
      this.authService.getUserInfo(username).subscribe(
        info => {
          this.userInfo = info;
          this.updatedUserInfo = { ...info };
        },
        error => {
          console.error('Erreur lors de la récupération des informations utilisateur', error);
        }
      );
    }
  }

  updateUser() {
    if (this.userInfo) {
      this.authService.updateUser({ ...this.userInfo, ...this.updatedUserInfo }).subscribe(
        response => {
          console.log('User updated successfully', response);
          this.userInfo = response;
        },
        error => {
          if (error.status === 403) {
            console.error('Access is forbidden: Check your token and user permissions.');
          } else {
            console.error('An error occurred:', error);
          }
        }
      );
    }
  }

  changeView(view: string) {
    this.currentView = view;
  }

  loadAllEvents() {
    this.eventService.getEvents().subscribe(
      (events: Event[]) => {
        console.log('Events reçus:', events);
        events.forEach(event => {
          console.log('Date brute de l\'événement:', event.date);
        });
        this.events = events;
      },
      error => {
        console.error('Erreur lors du chargement des événements', error);
      }
    );
  }

  toggleEventSelection(event: Event) {
    const index = this.selectedEvents.findIndex(e => e.id === event.id);
    if (index > -1) {
      this.selectedEvents.splice(index, 1);
    } else {
      this.selectedEvents.push(event);
    }
  }

  saveHomePageEvents() {
    this.eventService.saveHomePageEvents(this.selectedEvents).subscribe(
      response => {
        console.log('Home page events saved successfully', response);
      },
      error => {
        console.error('Error saving home page events', error);
      }
    );
  }

  searchEvents() {
    this.eventService.searchEvents(this.date, this.location, this.keyword).subscribe(
      (events: Event[]) => {
        console.log('Résultats de la recherche:', events);
        this.events = events;
      },
      error => {
        console.error('Erreur lors de la recherche des événements', error);
      }
    );
  }

  bookEvent(eventId: number) {
    this.selectedEventId = eventId;
    this.showReservationForm = true;
  }

  reserveEvent() {
    if (this.selectedEventId !== null) {
      this.eventService.createReservation(this.selectedEventId, this.numberOfTickets).subscribe(
        (response) => {
          console.log('Reservation successful', response);
          this.showReservationForm = false;
        },
        (error) => {
          console.error('Error making reservation', error);
          if (error.status === 403) {
            console.error('Authentication error. Please log in again.');
            // Redirect to login page or show login modal
          } else {
            console.error('An unexpected error occurred:', error.message);
          }
        }
      );
    }
  }

  cancelReservation() {
    this.showReservationForm = false;
    this.selectedEventId = null;
    this.numberOfTickets = 1;
  }
}

<h2>Tableau de bord utilisateur</h2>
<div class="container">
  <h1>Événements à venir</h1>
  <mat-grid-list cols="3" rowHeight="350px">
    <mat-grid-tile *ngFor="let event of events">
      <mat-card class="event-card">
        <mat-card-header>
          <mat-card-title>{{event.name}}</mat-card-title>
          <mat-card-subtitle>{{event.date | date:'dd/MM/yyyy HH:mm'}}</mat-card-subtitle>
        </mat-card-header>
        <mat-card-content>
          <p>{{event.description}}</p>
        </mat-card-content>
        <mat-card-actions>
          <button mat-button color="primary" ><a [routerLink]="['/event', event.id]">Voir les détails</a></button>
          <button mat-button color="accent" (click)="bookEvent(event.id)">Réserver</button>        </mat-card-actions>
      </mat-card>
    </mat-grid-tile>
  </mat-grid-list>
  <!-- Reservation Form -->
  <div *ngIf="showReservationForm" class="reservation-form">
    <h3>Réserver l'événement</h3>
    <form (ngSubmit)="reserveEvent()" #reservationForm="ngForm">
      <mat-form-field appearance="fill">
        <mat-label>Nombre de billets</mat-label>
        <input matInput type="number" [(ngModel)]="numberOfTickets" name="numberOfTickets" required min="1">
      </mat-form-field>

      <mat-form-field appearance="fill">
        <mat-label>Nom</mat-label>
        <input matInput [(ngModel)]="reservationName" name="reservationName" required>
      </mat-form-field>

      <mat-form-field appearance="fill">
        <mat-label>Email</mat-label>
        <input matInput type="email" [(ngModel)]="reservationEmail" name="reservationEmail" required email>
      </mat-form-field>

      <mat-form-field appearance="fill">
        <mat-label>Téléphone</mat-label>
        <input matInput [(ngModel)]="reservationPhone" name="reservationPhone">
      </mat-form-field>

      <mat-form-field appearance="fill">
        <mat-label>Commentaires</mat-label>
        <textarea matInput [(ngModel)]="reservationComments" name="reservationComments"></textarea>
      </mat-form-field>

      <div class="form-actions">
        <button mat-raised-button color="primary" type="submit" [disabled]="!reservationForm.form.valid">Réserver</button>
        <button mat-button type="button" (click)="cancelReservation()">Annuler</button>
      </div>
    </form>
  </div>
<nav>
  <ul>
    <li><a (click)="changeView('profile')">Profil</a></li>
    <li><a (click)="changeView('home')">Gestion Accueil</a></li>
    <li><a (click)="changeView('events')">Mes Événements</a></li>
    <li><a (click)="changeView('search')">Recherche d'événements</a></li>

  </ul>
</nav>
<div [ngSwitch]="currentView">
  <!-- Gardez les autres cas de switch existants -->

  <div *ngSwitchCase="'search'">
    <h3>Recherche d'événements</h3>
    <form (ngSubmit)="searchEvents()">
      <input [(ngModel)]="date" name="date" type="date" placeholder="Date">
      <input [(ngModel)]="location" name="location" type="text" placeholder="Lieu">
      <input [(ngModel)]="keyword" name="keyword" type="text" placeholder="Mot-clé">
      <button type="submit">Rechercher</button>
    </form>

    <div *ngFor="let event of events">
      <h4>{{ event.name }}</h4>
      <p>Date: {{ event.date | date:'dd/MM/yyyy HH:mm' }}</p>
      <p>Lieu: {{ event.location }}</p>
      <a [routerLink]="['/event', event.id]">Voir les détails</a>
    </div>
  </div>
</div>
<div [ngSwitch]="currentView">
  <div *ngSwitchCase="'profile'">
    <h3>Mon Profil</h3>
    <div *ngIf="userInfo">
      <p>Nom d'utilisateur : {{ userInfo.username }}</p>
      <p>Rôle : {{ userInfo.role }}</p>
      <p>Email : {{ userInfo.email }}</p>

      <h4>Mettre à jour les informations</h4>
      <form (ngSubmit)="updateUser()">
        <label for="username">Nom d'utilisateur:</label>
        <input id="username" [(ngModel)]="updatedUserInfo.username" name="username" required>

        <label for="email">Email:</label>
        <input id="email" [(ngModel)]="updatedUserInfo.email" name="email" required>

        <button type="submit">Mettre à jour</button>
      </form>
    </div>
  </div>

  <div *ngSwitchCase="'home'">
    <h3>Gestion de la Page d'Accueil</h3>
    <p>Sélectionnez les événements à afficher sur la page d'accueil :</p>
    <ul>
      <li *ngFor="let event of events">
        <input type="checkbox" (change)="toggleEventSelection(event)" [checked]="selectedEvents.includes(event)">
        {{ event.name }} - {{ event.date | date }}
      </li>
    </ul>
    <button (click)="saveHomePageEvents()">Enregistrer les changements</button>
  </div>
  <div *ngSwitchCase="'events'">
    <h3>Mes Événements</h3>
    <ul>
      <li *ngFor="let event of events">
        <span *ngIf="event.isUserEvent" style="color: green;">(Votre événement)</span>
        {{ event.name }} - {{ event.date | date:'dd/MM/yyyy HH:mm' }} - {{ event.location }}
      </li>
    </ul>
  </div>
</div>
</div>
