import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { EventService } from '../event.service';

interface Event {
  id: number;
  name: string;
  date: string;
  location: string;
  description: string;
  // Ajoutez d'autres propriétés selon votre modèle d'événement
}

@Component({
  selector: 'app-event-details',
  templateUrl: './event-details.component.html',
  styleUrls: ['./event-details.component.scss']
})
export class EventDetailsComponent implements OnInit {
  event: Event | null = null;
  error: string | null = null;

  constructor(
    private route: ActivatedRoute,
    private eventService: EventService
  ) { }

  ngOnInit() {
    this.route.params.subscribe(params => {
      const eventId = +params['id']; // Le '+' convertit le paramètre en nombre
      this.loadEventDetails(eventId);
    });
  }

  loadEventDetails(id: number) {
    this.eventService.getEventDetails(id).subscribe(
      (eventData: Event) => {
        this.event = eventData;
      },
      (error) => {
        console.error('Erreur lors du chargement des détails de l\'événement', error);
        this.error = 'Impossible de charger les détails de l\'événement.';
      }
    );
  }
}
<p>event-details works!</p>
<div *ngIf="event">
  <h2>{{ event.name }}</h2>
  <p>Date: {{ event.date | date:'dd/MM/yyyy HH:mm' }}</p>
  <p>Location: {{ event.location }}</p>
  <p>Description: {{ event.description }}</p>
  <!-- Add other details as per your event model -->
</div>

<div *ngIf="error">
  <p>{{ error }}</p>
</div>

<div *ngIf="!event && !error">
  <p>Loading event details...</p>
</div>
