export class UserDashboardComponent {
  constructor(authService, eventService) {
    this.authService = authService;
    this.eventService = eventService;
    this.userInfo = null;
    this.updatedUserInfo = {};
    this.currentView = 'profile';
    this.events = [];
    this.selectedEvents = [];
    this.date = '';
    this.location = '';
    this.keyword = '';
    this.selectedEventId = null;
    this.numberOfTickets = 1; // Default number of tickets
    this.reservationSuccess = false;
    this.reservationError = '';
  }
  ngOnInit() {
    this.loadUserInfo();
    this.loadAllEvents();
  }
  parseDate(dateString) {
    const [datePart, timePart] = dateString.split(' ');
    const [day, month, year] = datePart.split('/').map(Number);
    const [hours, minutes] = timePart.split(':').map(Number);
    return new Date(year, month - 1, day, hours, minutes);
  }
  loadUserInfo() {
    const username = this.authService.getUsername();
    if (username) {
      this.authService.getUserInfo(username).subscribe(info => {
        this.userInfo = info;
        this.updatedUserInfo = {
          ...info
        };
      }, error => {
        console.error('Erreur lors de la récupération des informations utilisateur', error);
      });
    }
  }
  updateUser() {
    if (this.userInfo) {
      this.authService.updateUser({
        ...this.userInfo,
        ...this.updatedUserInfo
      }).subscribe(response => {
        console.log('User updated successfully', response);
        this.userInfo = response;
      }, error => {
        if (error.status === 403) {
          console.error('Access is forbidden: Check your token and user permissions.');
        } else {
          console.error('An error occurred:', error);
        }
      });
    }
  }
  bookEvent(eventId) {
    this.selectedEventId = eventId;
    this.currentView = 'reservation';
  }
  changeView(view) {
    this.currentView = view;
  }
  loadAllEvents() {
    this.eventService.getEvents().subscribe(events => {
      console.log('Events reçus:', events);
      events.forEach(event => {
        console.log('Date brute de l\'événement:', event.date);
      });
      this.events = events;
    }, error => {
      console.error('Erreur lors du chargement des événements', error);
    });
  }
  toggleEventSelection(event) {
    const index = this.selectedEvents.findIndex(e => e.id === event.id);
    if (index > -1) {
      this.selectedEvents.splice(index, 1);
    } else {
      this.selectedEvents.push(event);
    }
  }
