import { Component } from '@angular/core';
import { EventService } from '../event.service';

@Component({
  selector: 'app-event-search',
  templateUrl: './event-search.component.html',
  styleUrls: ['./event-search.component.css']
})
export class EventSearchComponent {
  date: string = '';
  location: string = '';
  keyword: string = '';
  events: any[] = [];

  constructor(private eventService: EventService) { }

  searchEvents() {
    this.eventService.searchEvents(this.date, this.location, this.keyword)
      .subscribe(
        (data) => {
          this.events = data;
        },
        (error) => {
          console.error('Error fetching events', error);
        }
      );
  }
}
<h2>Search Events</h2>
<form (ngSubmit)="searchEvents()">
  <input [(ngModel)]="date" name="date" type="date" placeholder="Date">
  <input [(ngModel)]="location" name="location" type="text" placeholder="Location">
  <input [(ngModel)]="keyword" name="keyword" type="text" placeholder="Keyword">
  <button type="submit">Search</button>
</form>

<div *ngFor="let event of events">
  <h3>{{ event.name }}</h3>
  <p>{{ event.description }}</p>
  <p>Date: {{ event.dateTime | date }}</p>
  <p>Location: {{ event.location }}</p>
  <a [routerLink]="['/event', event.id]">View Details</a>
</div><p>event-search works!</p>
