import { Component, OnInit } from '@angular/core';
import { AuthService } from '../auth.service';
import {HttpErrorResponse} from "@angular/common/http";

@Component({
  selector: 'app-admin-dashboard',
  templateUrl: './admin-dashboard.component.html',
  styleUrls: ['./admin-dashboard.component.css']
})
export class AdminDashboardComponent implements OnInit {
  userInfo: any;
  users: any[] = [];

  constructor(private authService: AuthService) { }

  ngOnInit() {
    this.loadAdminInfo();
    this.loadUsers();
  }

  loadAdminInfo() {
    const username = this.authService.getUsername();
    if (username) {
      this.authService.getUserInfo(username).subscribe(
        info => {
          this.userInfo = info;
        },
        error => {
          console.error('Erreur lors de la récupération des informations utilisateur', error);
        }
      );
    }
  }

  loadUsers() {
    this.authService.getAllUsers().subscribe(
      users => {
        this.users = users;
      },
      error => {
        console.error('Erreur lors du chargement des utilisateurs', error);
      }
    );
  }


  updateUser(user: any) {
    this.authService.updateUser(user).subscribe(
      updatedUser => {
        console.log('Utilisateur mis à jour', updatedUser);
        this.loadUsers(); // Recharger la liste après la mise à jour
      },
      error => {
        console.error('Erreur lors de la mise à jour de l\'utilisateur', error);
      }
    );
  }

  deleteUser(id: number) {
    this.authService.deleteUser(id).subscribe(
      () => {
        console.log('Utilisateur supprimé');
        this.loadUsers(); // Recharger la liste après la suppression
      },
      error => {
        console.error('Erreur lors de la suppression de l\'utilisateur', error);
      }
    );
  }
}
<p>admin-dashboard works!</p>
<h2>Informations de l'administrateur</h2>
<div *ngIf="userInfo">
  <p>Nom d'utilisateur : {{ userInfo.username }}</p>
  <p>Email : {{ userInfo.email }}</p>
  <p>Rôle : {{ userInfo.role }}</p>
  <button (click)="updateUser(userInfo)">Modifier</button>
</div>

<h2>Gestion des utilisateurs</h2>
<table>
  <thead>
  <tr>
    <th>ID</th>
    <th>Nom d'utilisateur</th>
    <th>Email</th>
    <th>Rôle</th>
    <th>Actions</th>
  </tr>
  </thead>
  <tbody>
  <tr *ngFor="let user of users">
    <td>{{ user.id }}</td>
    <td>{{ user.username }}</td>
    <td>{{ user.email }}</td>
    <td>{{ user.role }}</td>
    <td>

      <button (click)="deleteUser(user.id)">Supprimer</button>
    </td>
  </tr>
  </tbody>
</table>
