import { Component, OnInit } from '@angular/core';
import { AuthService } from '../auth.service';

@Component({
  selector: 'app-user-dashboard',
  templateUrl: './user-dashboard.component.html',
  styleUrls: ['./user-dashboard.component.css']
})
export class UserDashboardComponent implements OnInit {
  userInfo: any;
  updatedUserInfo: any = {};

  constructor(private authService: AuthService) { }

  ngOnInit() {
    const username = this.authService.getUsername();
    if (username) {
      this.authService.getUserInfo(username).subscribe(
        info => {
          this.userInfo = info;
          this.updatedUserInfo = { ...info }; // Copier les infos utilisateur pour mise à jour
        },
        error => {
          console.error('Erreur lors de la récupération des informations utilisateur', error);
        }
      );
    }
  }

  updateUser() {
    this.authService.updateUser(this.updatedUserInfo).subscribe(
      updatedUser => {
        console.log('Informations utilisateur mises à jour', updatedUser);
        this.userInfo = updatedUser;
      },
      error => {
        console.error('Erreur lors de la mise à jour des informations utilisateur', error);
      }
    );
  }

  deleteUser() {
    if (confirm('Êtes-vous sûr de vouloir supprimer votre compte ? Cette action est irréversible.')) {
      this.authService.deleteUser(this.userInfo.id).subscribe(
        () => {
          console.log('Compte utilisateur supprimé');
          // Rediriger l'utilisateur vers la page de login ou d'accueil après suppression
        },
        error => {
          console.error('Erreur lors de la suppression du compte utilisateur', error);
        }
      );
    }
  }
}
<p>user-dashboard works!</p>
<h2>Tableau de bord utilisateur</h2>
<div *ngIf="userInfo">
  <p>Nom d'utilisateur : {{ userInfo.username }}</p>
  <p>Rôle : {{ userInfo.role }}</p>
  <p>Email : {{ userInfo.email }}</p>

  <h3>Mettre à jour les informations</h3>
  <form (ngSubmit)="updateUser()">
    <label for="username">Nom d'utilisateur:</label>
    <input id="username" [(ngModel)]="updatedUserInfo.username" name="username" required>

    <label for="email">Email:</label>
    <input id="email" [(ngModel)]="updatedUserInfo.email" name="email" required>

    <button type="submit">Mettre à jour</button>
  </form>

  <button (click)="deleteUser()">Supprimer mon compte</button>
</div>
